mapper.select:
select count(*)
from users
where user_id=#{id}

SELECT COUNT(*)
FROM USERS
WHERE USER_NAME =#{userName} AND USER_PHONE =#{userPhone}

select count(*)
from subscribe
where creater_no = #{userNo}

mapper.update:
update users
set user_name = #{userName}, user_nickname = #{userNickname}, user_phone = #{userPhone}, user_email = #{userEmail}, user_birth = #{userBirth}, user_gender = #{userGender}, user_comment = #{userComment}
where user_no = #{userNo}

mapper.select:
SELECT USER_ID, USER_NAME
FROM users
WHERE USER_NAME =#{userName} AND USER_PHONE =#{userPhone} AND USER_EMAIL =#{userEmail}
